import React from 'react'
import {connect} from 'react-redux'
import {fetchStream} from '../actions'
import streams from '../apis/streams'


class Comments extends React.Component {
	state = {comments: [], text: ""} 
	async componentDidMount(){ 
		const comments = await streams.get('/comments/') 
		this.setState({comments: comments.data})
		console.log(this.state.comments)

	}

	renderComments(){
		return(

			this.state.comments.map((comment) => { 
				if(comment.streamId === this.props.stream.id){
					return(
						<div key={comment.id} className="item">
							<div className="content">
							    <div className="header">{comment.user}</div>
							    {comment.content}
							</div>
						</div>
					);
				}else{
					return null
				}
			})

		);
	}

	onSubmit = async (e) => { 
		e.preventDefault()
		const response = await streams.post('/comments', {
			user: this.props.user,
			content: this.state.text,
			streamId: this.props.stream.id

		})

		this.setState({comments: response.data})
	}


	render(){ 


			return(

				<div className="ui segment" style={{width: "72.5%"}}>
					<div className="ui relaxed divided list">
						<h5>Chat</h5>
						{this.renderComments()} 
		    		</div>
		    		<div className="ui divider"></div>
		    		<div className="ui search">
		    			<form className="ui form" onSubmit={this.onSubmit}>
		    				<input onChange={(e) => {this.setState({text: e.target.value})}} style={{width: "100%"}} className="prompt" type="text" placeholder="Add Comment..."/>
		    			</form>


					</div>
				</div>
			);




	}


}

const mapStateToProps = (state) => {
	return ({user: state.signedIn.userId}); 
}


export default connect(mapStateToProps)(Comments);
