const INITIAL_STATE = {
	signedIn: null, 
	userId: null 
}

export default (state = INITIAL_STATE, action) => { 
	switch(action.type){
		case 'SIGN_IN':

			return {...state, signedIn: true , userId: action.payload}

		case 'SIGN_OUT':

			return {...state, signedIn: false , userId: null}
		default: 
			return state
	}
}
