import React from 'react'
import {connect} from 'react-redux'
import {fetchStreams} from '../../actions'

class StreamList extends React.Component {

	componentDidMount(){
		this.streams = this.props.fetchStreams()
	}

	render(){
		console.log(this.props.loggedInAs)
		return(
			<div>
			<h2>Streams</h2>
			<div className="ui celled list">{this.renderList()}</div>
			<div style={{float: 'middle'}}>{this.renderCreate()}</div>
			</div>
		);

	}

	renderAdmin(stream){
		console.log('stream id:  ' , stream.userId , 'loggedInAs: ' , this.props.loggedInAs)
		if(stream.userId === this.props.loggedInAs){ 
			return(
				<div className="right floated content">
					<button className="ui button primary">Edit</button>
					<button className="ui button red">Delete</button>
				</div>
			);
		}
	}

	renderCreate(){
		if(this.props.isLoggedIn){ 
			return(
				<div>
					<button className="ui button">Create Stream</button>
				</div>
			);

		}
	} 

	renderList(){


			return(
				this.props.streams.map(stream => {
					return(
						<div className="item" key={stream.id} >
						{this.renderAdmin(stream)}
							<i className="large middle aligned icon camera" /> 
							<div className="content">{stream.title}
								<div className="description">
									{stream.description}
								</div>
							</div>

						</div>

				)})

			);

	}

}

const mapStateToProps = (state) => { 
	return {
				streams: Object.values(state.streams),
				loggedInAs: state.signedIn.userId,
				isLoggedIn: state.signedIn.signedIn 
		 	}
}

export default connect(mapStateToProps, {fetchStreams})(StreamList);
