import React from 'react';

class GoogleAuth extends React.Component { 
		state = {signedIn: null}

		componentDidMount(){ 
			window.gapi.load('client:auth2', () => {
				window.gapi.client.init({
					clientId: '479040910300-7psce3pfiudgm5mn3ko8qlif9i57mbks.apps.googleusercontent.com',
					scope: 'email '
				}).then(() => {
					this.auth = window.gapi.auth2.getAuthInstance() 
					this.setState({signedIn: this.auth.isSignedIn.get()}); 
					this.auth.isSignedIn.listen(() => {this.setState({signedIn: this.auth.isSignedIn.get()})})
				}); 
			})
		}

		renderAuthButton(){ 	
			if(this.state.signedIn === null){
				return(null);

			} else if (this.state.signedIn){
				return(<div><button className='ui red google button'><i className="google icon"/>Sign Out</button></div>) 
			}
			else {
				return(<div><button className='ui green google button'><i className="google icon"/>Sign In with Google</button></div>);
			}

		}

		onSignIn = () => {
			if(this.state.signedIn){
				this.auth.signOut()
			}
			else{
				this.auth.signIn() 
			}
		}

		render() {
			return(
				this.renderAuthButton()
			) 
		}
}

export default GoogleAuth
